var fs = require('fs');

var file = fs.createReadStream('./message.txt');

file.on('open',function(fd){
  console.log('开始读取文件')
})

file.on('data',function(data){
    console.log('读取到数据')
    console.log(data)
})

file.on('end',function(){
  console.log('文件读取完毕')
})

file.on('err',function(err){
    console.log('文件读取失败')
})
